def count_handles(handles)
    return handles.length
end 

def shortest_handle(handles)
    shortest_handle = handles.min_by { |handle| handle.length }
    return shortest_handle
end

def count_handle5(handles)
    count_handle5 = handles.count { |handle| handle.length == 6 }
    return count_handle5
end

def starts_with_capital(handles)
    handles.count { |handle| handle[1].match?(/\A[A-Z]/) }
end

def alphabetical_order(handles)
    handles.sort
    end

def tri_handles_croissant(handles)
    return handles.sort { |handle| handle.length }
end

def position_epenser(handles, epenser)
    handles.index(epenser)
end

def handle_length_distribution(handles)
    distribution = Hash.new(0)
    handles.each { |handle| distribution[handle.length - 1] += 1 }
    return distribution.sort_by { |length, count| length }.to_h
end

